import 'isomorphic-fetch'
import Link from 'next/link'
export default class extends React.Component {
  static async getInitialProps() {
    let [reqEnglish, reqSpanish] = await Promise.all([
      fetch('https://api.audioboom.com/channels/recommended'),
      fetch(`https://api.audioboom.com/channels/recommended?category_ids[]=179`)
    ])

    //   Llama el canal con el id especificado
    let dataEnglish = await reqEnglish.json()
    let english = dataEnglish.body
    let dataSpanish = await reqSpanish.json()
    let spanish = dataSpanish.body

    return { english, spanish }
  }

  render() {
    const { english, dataSpanish } = this.props
    console.log(english)
    return (
      <div>
        <header>Podcasts</header>
        <h2>English</h2>
        <div className="channels">
          {english.map((englishChannel) => (
            <div key={englishChannel.id}>
              <Link href={`/channel?id=${englishChannel.id}`}>
                <a className="channel">
                  <img
                    src={englishChannel.urls.logo_image.original}
                    alt="channel logo"
                  />
                </a>
              </Link>
              <h2>{englishChannel.title}</h2>
            </div>
          ))}
        </div>
        <div className="channels">
          <h2>Español</h2>
          {english.map((englishChannel) => (
            <div key={englishChannel.id}>
              <Link href={`/channel?id=${englishChannel.id}`}>
                <a className="channel">
                  <img
                    src={englishChannel.urls.logo_image.original}
                    alt="channel logo"
                  />
                </a>
              </Link>
              <h2>{englishChannel.title}</h2>
            </div>
          ))}
        </div>

        <style jsx>{`
          .channels {
            display: grid;
            grid-gap: 15px;
            padding: 15px;
            grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
          }
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
          }
          .channel {
             {
              /* max-width: 1000px; */
            }
             {
              /* margin: 0 auto 0.5em; */
            }
            display: block;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
            text-align: center;
            margin-bottom: 0.5em;
          }
          .channel img {
            width: 100%;
          }
          .h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
          }
        `}</style>
        <style jsx global>{`
          body {
            margin: 0;
            font-family: system-ui;
            background: white;
          }
          a {
            text-decoration: none;
          }
        `}</style>
      </div>
    )
  }
}
