import 'isomorphic-fetch'
import Link from 'next/link'
import Layout from '../components/Layout'
export default class extends React.Component {
  static async getInitialProps() {
    let [reqEnglish, reqSpanish] = await Promise.all([
      fetch('https://api.audioboom.com/channels/recommended'),
      fetch(`https://api.audioboom.com/channels/recommended?category_ids[]=179`)
    ])

    //   Llama el canal con el id especificado
    let dataEnglish = await reqEnglish.json()
    let english = dataEnglish.body
    let dataSpanish = await reqSpanish.json()
    let spanish = dataSpanish.body

    return { english, spanish }
  }

  render() {
    const { english, spanish } = this.props
    return (
      <Layout title="Podcasts">
        <h2>English</h2>
        <div className="channels">
          {english.map((englishChannel) => (
            <div key={englishChannel.id}>
              <Link href={`/channel?id=${englishChannel.id}`}>
                <a className="channel">
                  <img
                    src={englishChannel.urls.logo_image.original}
                    alt="channel logo"
                  />
                </a>
              </Link>
              <h2>{englishChannel.title}</h2>
            </div>
          ))}
        </div>
        <h2>Español</h2>
        <div className="channels">
          {spanish.map((spanishChannel) => (
            <div key={spanishChannel.id}>
              <Link href={`/channel?id=${spanishChannel.id}`}>
                <a className="channel">
                  <img
                    src={spanishChannel.urls.logo_image.original}
                    alt="channel logo"
                  />
                </a>
              </Link>
              <h2>{spanishChannel.title}</h2>
            </div>
          ))}
        </div>

        <style jsx>{`
          .channels {
            display: grid;
            grid-gap: 15px;
            padding: 15px;
            grid-template-columns: repeat(auto-fill, minmax(160px, 1fr));
          }
          .channel {
            display: block;
            margin-bottom: 0.5em;
            color: #333;
            text-decoration: none;
          }
          .channel img {
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
            width: 100%;
          }
          h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
            text-align: center;
          }
        `}</style>
      </Layout>
    )
  }
}
