              <p>
                <Link href="/">
                  <a>Volver al Inicio</a>
                </Link>
              </p>
            </div>
          ) : (
            <div className="message">
              <h1>Hubo un problema ðŸ˜–</h1>
              <p>Intenta nuevamente en unos segundos</p>
            </div>
          )}
        </Layout>

        <style jsx>{`
          .message {
            padding: 100px 30px;
            text-align: center;
          }
          h1 {
            margin-bottom: 2em;
          }
          a {
            color: #8756ca;
            padding: 1em 2em;
            background-color: #2a0a22;
            border-radius: 20px;
          }
        `}</style>
      </div>
    )
  }
}
