import 'isomorphic-fetch'
export default class extends React.Component {
  static async getInitialProps() {
    let req = await fetch('https://api.audioboom.com/channels/recommended')
    let { body: channels } = await req.json()

    return { channels }
  }

  render() {
    const { channels, title, audio } = this.props
    return (
      <div>
        <header>Podcasts</header>
        <div className="channels">
          {channels.map((channel) => (
            <div className="channel">
              <h2>{channel.title}</h2>
              <img src={channel.urls.logo_image.original} alt="channel logo" />
            </div>
          ))}
        </div>

        <style jsx>{`
          .channels {
            display: grid;
            grid-gap: 15px;
            padding: 15px;
            grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
          }
          header {
            color: #fff;
            background: #8756ca;
            padding: 15px;
          }
          .channel {
             {
              /* max-width: 1000px; */
            }
             {
              /* margin: 0 auto 0.5em; */
            }
            display: block;
            border-radius: 3px;
            box-shadow: 0px 2px 6px rgba(0, 0, 0, 0.15);
            text-align: center;
            margin-bottom: 0.5em;
            min-height: 400px;
            position: relative;
          }
          .channel img {
            width: 100%;
            position: absolute;
            bottom: 0;
          }
          .h2 {
            padding: 5px;
            font-size: 0.9em;
            font-weight: 600;
            margin: 0;
          }
        `}</style>
        <style jsx global>{`
          body {
            margin: 0;
            font-family: system-ui;
            background: white;
          }
        `}</style>
      </div>
    )
  }
}
