        <p>Curso de Next.js</p>
        <span>
          <img src="/platzi-logo.webp" alt="Platzi" />
          <p>Platzi</p>
        </span>

        <style jsx>{`
          div {
            display: flex;
            flex-direction: column;
            justify-content: center;
          }
          img {
            max-width: 100%;
          }
        `}</style>
      </div>
    )
  }
}
