import 'isomorphic-fetch'
import Link from 'next/link'
import Layout from '../components/Layout'
import PodcastList from '../components/PodcastList'
import ChannelGrid from '../components/ChannelGrid'

export default class extends React.Component {
  static async getInitialProps({ query }) {
    try {
      let idChannel = query.id

      let [reqChannel, reqAudios, reqSeries] = await Promise.all([
        fetch(`https://api.audioboom.com/channels/${idChannel}`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/audio_clips`),
        fetch(`https://api.audioboom.com/channels/${idChannel}/child_channels`)
      ])

      //   Llama el canal con el id especificado
      let dataChannel = await reqChannel.json()
      let channel = dataChannel.body.channel

      //   LLama los archivos de audio principales del podcast
      let dataAudios = await reqAudios.json()
      let audio_clips = dataAudios.body.audio_clips

      //   LLama los archivos de audio de subseries del podcast
      let dataSeries = await reqSeries.json()
      let series = dataSeries.body.channels
      return { channel, audio_clips, series }
    } catch (e) {
      return {
        channel: null,
        audio_clips: null,
        series: null,
        statusCode: 503
      }
    }
  }
  render() {
    const { channel, audio_clips, series } = this.props
    return (
      <Layout title={channel.title}>
        {channel.urls.banner_image.original != null ? (
          <img
            className="banner"
            src={channel.urls.banner_image.original}
            alt={`${channel.title} banner`}
          />
        ) : (
          <div className="defaultBanner">
            <h1>{channel.title}</h1>
          </div>
        )}
        <div className="descriptionContainer">
          <p className="description">{channel.description}</p>
        </div>

        {audio_clips > [] ? (
          <PodcastList audio_clips={audio_clips}></PodcastList>
        ) : null}

        {series > [] ? (
          <ChannelGrid channels={series} language="Series" />
        ) : null}

        <style jsx>{`
          .defaultBanner {
            display: flex;
            justify-content: center;
            align-items: center;
            background-color: #2a0a22;
            box-shadow: 0 0 15px 0 rgba(0, 0, 0, 0.5);
          }
          .defaultBanner h1 {
            font-size: 7em;
            text-align: center;
            color: #bbb;
          }
          .banner {
            width: 100%;
          }
          .descriptionContainer {
            border-radius: 40px;
            background-color: #2a0a22;
            max-height: 150px;
            max-width: 500px;
            margin: 2em auto;
            padding: 1em 2em;
            box-shadow: 3px 4px 3px rgba(0, 0, 0, 0.5);
          }
          .description {
            max-height: 100px;
            overflow-y: scroll;
            text-align: center;
            color: #eee;
          }
        `}</style>
      </Layout>
    )
  }
}
